import React, { Component, useState } from 'react';
import {
    Box,
    Button,
    Heading,
    Grommet,
    Grid,
    Text,
} from 'grommet';

import './App.css';

const theme = {
    global: {
        colors: {
            brand: '#272343',
            focus: '#272343'
        },
        Button: {
            active: {
                background: { color: "aliceblue" }
            }
            },
            font: {
                family: 'Lato',
            },
        },
    };

    const SidebarButton = ({ label, ...rest }) => (
        <Button active={true} plain {...rest}>
            {({ hover }) => (
                <Box
                    background={hover ? "#fce149" : undefined}
                    pad={{ horizontal: "large", vertical: "medium" }}
                >
                    <Text size="large">{label}</Text>
                </Box>
            )}
        </Button>
    );

    const SidebarButtons = () => {
        const [active, setActive] = useState();
        return (
            <Grommet full theme={theme}>
                <Box fill direction="row">
                    <Box background="brand">
                        {["Appointments", "View Patients", "Settings", "Sign Out"].map(label => (
                            <SidebarButton
                                key={label}
                                label={label}
                                active={label === active}
                                onClick={() => {
                                    if (label === "Appointments") {
                                        window.location = "/ApptList"
                                    }
                                    else if (label === "Sign Out") {
                                        fetch("http://localhost:3001/endSession");
                                        window.location = "/"
                                    }
                                    else if (label === "Settings") {
                                        window.location = "/DocSettings"
                                    }
                                    else if (label === "View Patients") {
                                        window.location = "/MedHistView"
                                    }
                                    setActive(label);
                                }}
                            />
                        ))}

                    </Box>
                </Box>
            </Grommet>
        );
    };

    export class DocHome extends Component {
        componentDidMount() {
        }

render() {
    const Header = () => (
        <Box
            tag='header'
            background='brand'
            pad='medium'
            elevation='small'
            justify='center'
            direction='row'
            align='center'
            flex={false}
            style={{ borderBottom: "1px solid grey" }}
        >
            <a style={{ color: 'inherit', textDecoration: 'inherit' }} href="/"><Heading level='3' margin='none'>Hospital appointment booking management system</Heading></a>

        </Box>
    );

    return (
        <Grommet full={true}
            theme={theme}>
            <Box align="left">
                <Header />
                <Grid
                    fill
                    rows={['auto', 'flex']}
                    columns={['auto', 'flex']}
                    areas={[
                        { name: 'sidebar', start: [0, 1], end: [0, 1] },
                        { name: 'main', start: [1, 1], end: [1, 1] },
                    ]}>
                    <Box
                        gridArea="sidebar"
                        width="small"
                        animation={[
                            { type: 'fadeIn', duration: 300 },
                            { type: 'slideRight', size: 'xlarge', duration: 150 },
                        ]}
                    >
                        <SidebarButtons />
                    </Box>
                    <Box
                        gridArea="main"
                        justify="top"
                        align="center">
                        <Box align="center" pad="large">
                            <Heading
                                color="#000000">Welcome Doctor
                            </Heading>
                        </Box>
                    </Box>
                </Grid>
            </Box>
        </Grommet>
    );
}
}

export default DocHome;
